mapper.update:
UPDATE PRJ_TASK
   SET TSTITLE=#{tstitle}
     , TSSTARTDATE=#{tsstartdate} 
     , TSENDDATE=#{tsenddate}
     , TSENDREAL=#{tsendreal}
     , TSRATE=#{tsrate}
 WHERE DELETEFLAG='N'
   AND TSNO=#{tsno}

mapper.delete:
UPDATE PRJ_TASK
   SET DELETEFLAG='Y'
 WHERE TSNO=#{tsno}
